DROP DATABASE IF EXISTS `market_news_analyse`; -- 库名与项目名保持一致
CREATE DATABASE IF NOT EXISTS `market_news_analyse` DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci;

USE `market_news_analyse`;


-- 创建舆情词典
DROP TABLE IF EXISTS `news_word`;
CREATE TABLE `news_word` (
  `id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键id, 必备字段',
  `gmt_create` DATETIME DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间, 必备字段',
  `gmt_modified` DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间, 必备字段',
  
  `word` VARCHAR(100) NOT NULL COMMENT '舆情词',
  `word_hash` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '舆情词对应的hashcode',
  `news_word_grade_id` INT(11) DEFAULT 0 COMMENT '舆情值',
  PRIMARY KEY (`id`)
) ENGINE=INNODB DEFAULT CHARSET=utf8;


-- 创建舆情值对应的表
DROP TABLE IF EXISTS `news_word_grade`;
CREATE TABLE `news_word_grade` (
  `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT COMMENT '主键id, 必备字段',
  `gmt_create` DATETIME DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间, 必备字段',
  `gmt_modified` DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间, 必备字段',
  
  `word_grade_name` VARCHAR(100) NOT NULL COMMENT '舆情词等级名称',
  `word_grade_value` INT NOT NULL DEFAULT 0 COMMENT '舆情词等级对应的舆情值',
  `mark` VARCHAR(100) DEFAULT NULL COMMENT '舆情值',
  PRIMARY KEY (`id`)
) ENGINE=INNODB DEFAULT CHARSET=utf8;

